export default class ErrorTip {
    private readonly _reason;
    private readonly _fixSuggestion;
    constructor(reason: string, fixSuggestion: string);
    /**
     * 原因
     */
    getReason(): string;
    /**
     * 解决措施
     */
    getFixSuggestion(): string;
    /**
     * 获取标准格式的错误信息
     */
    getMessage(): string;
}
